mapper.select:
select count(id) from library_book

     and `name` like concat('%', #{name},'%')
     and code like concat('%',#{code},'%')
     and `type` = #{type}
     and leavel = #{leavel}
     and claz = #{claz}
     and author = #{author}
     and mechanism = #{mechanism}
     and publication_date = #{publicationDate}
     and version = #{version}
     and circulation = #{circulation}
     and ISBN = #{isbn}
     and series like concat('%', #{series}, '%')
     and key_words like concat('%', #{keyWords}, '%')
     and price = #{price}
     and mark = #{mark}
     and status = #{status}
     and create_personal = #{createPersonal}
     and update_personal = #{updatePersonal}
    and is_del = '0'



     and `name` like concat('%', #{name},'%')
     and code like concat('%', #{code},'%')
     and `type` = #{type}
     and leavel = #{leavel}
     and claz = #{claz}
     and author = #{author}
     and mechanism = #{mechanism}
     and publication_date = #{publicationDate}
     and version = #{version}
     and circulation = #{circulation}
     and ISBN = #{isbn}
     and series like concat('%', #{series}, '%')
     and key_words like concat('%', #{keyWords}, '%')
     and price = #{price}
     and mark = #{mark}
     and status = #{status}
     and create_personal = #{createPersonal}
     and update_personal = #{updatePersonal}
     and is_del = '0'

order by create_time desc

mapper.update:
update library_book
`name` = #{name}, code = #{code}, `type` = #{type}, leavel = #{leavel}, claz = #{claz}, author = #{author}, mechanism = #{mechanism}, publication_date = #{publicationDate}, version = #{version}, circulation = #{circulation}, ISBN = #{isbn}, series = #{series}, key_words = #{keyWords}, price = #{price}, mark = #{mark}, other = #{other}, status = #{status}, create_personal = #{createPersonal}, create_time = #{createTime}, update_time = #{updateTime}, update_personal = #{updatePersonal},
where id = #{id}

update library_book
set is_del = '1',update_time = now() ,update_personal = #{operator} where id in

    #{id}

mapper.select:
select
    id,
    `name`,
    code,
    `type` as typeCode,
    leavel,
    claz as clazId,
    (select bbcc.`name` from base_book_class_cn as bbcc where claz = bbcc.id) as claz,
    author as authorId,
    (select la.`name` from library_auth as la where author = la.id) as author,
    mechanism as mechanismId,
    (select lp.`name` from library_press as lp where mechanism = lp.id) as mechanism,
    publication_date,
    `version`,
    circulation,
    ISBN,
    series,
    key_words,
    price,
    mark,
    other,
    `status`,
    create_personal as createPersonal,
    (select su.`user_name` from sys_user as su where create_personal = su.user_id and su.del_flag='0') as createPersonalName,
    update_personal as updatePersonal,
    (select su.`user_name` from sys_user as su where update_personal = su.user_id and su.del_flag='0') as updatePersonalName
from library_book

     and `name` = #{name}
     and code = #{code}
     and `type` = #{type}
     and leavel = #{leavel}
     and claz = #{claz}
     and author = #{author}
     and mechanism = #{mechanism}
     and publication_date = #{publicationDate}
     and version = #{version}
     and circulation = #{circulation}
     and ISBN = #{isbn}
     and series like concat('%', #{series}, '%')
     and key_words like concat('%', #{keyWords}, '%')
     and price = #{price}
     and mark = #{mark}
     and status = #{status}
     and create_personal = #{createPersonal}
     and update_personal = #{updatePersonal}
        and is_del = '0'

order by create_time desc
